angular.module("sbAdminApp").factory("Authentication",["$window",function($window){var auth={};return auth.init=function(){return $window.sessionStorage.user&&$window.sessionStorage.user&&(this.user=JSON.parse($window.sessionStorage.user)),this},auth.setUser=function(user){$window.sessionStorage.user=JSON.stringify(user),this.user=user},auth.clear=function(){$window.sessionStorage.clear()},auth.isAuthenticated=function(){return!!this.user},auth.init()}]);